import React from 'react';
const initialState = {
    user: null,
  };

const FormReducer = (state = initialState, action) =>{
    
    switch (action.type) { 
        case 'ADD_DATA': 
        return {
            ...state,
            user: [...user, action.user],
          }; 
            //return [...state, user: state.concat([action.user])
        case 'DELETE_DATA': 
            return state.filter((users)=>users.id !== action.id)
        case 'EDIT_DATA': 
            return state.map((users)=>users.id === action.id
            ? {...users,editing : !users.editing}
            :users    
            )
        case 'UPDATE_DATA': 
            return state.map((users)=>{
                if(users.id === action.id){
                    return{
                        ...users,
                        user_title: action.user.user_title,
                        user_firstname: action.user.user_firstname,
                        user_lastname: action.user.user_lastname,
                        user_birthday: action.user.user_birthday,
                        user_nationality: action.user.user_nationality,
                        user_citizenID:action.user.user_citizenID,
                        user_gender:action.user.user_gender,
                        user_phoneType: action.user.user_phoneType,
                        user_mobliephone: action.user.user_mobliephone,
                        user_passportNo: action.user.user_passportNo,
                        user_salary: action.user.user_salary,
                        editing:!users.editing
                    }
                }else{
                    return users
                }
            } 
            )
        default: 
            return state; 
    } 
    
}
 
export default FormReducer
