import React from 'react'
import {connect} from 'react-redux';
import { useDispatch , useSelector} from 'react-redux'; 

const List = (props) =>{
    const dispatch = useDispatch({}); 
    const { user } = useSelector((state) => state);
    console.log(user)  
    const handleDelete = (e) => { 
        dispatch({    
            type:'DELETE_DATA', 
            payload:e
        })  
    }
    const handleUpdate = (e) => { 
        dispatch({    
            type:'EDIT_DATA', 
            payload:e
        })  
    }
    return(
        <div className="mt-3">
            <div className="row">
                <input class="" type="checkbox" value="" id="inlineCheckbox1" />
                <label class="form-check-label" for="inlineCheckbox1">Select All</label>
                <input type="button" className="btn btn-danger pt-0 tb-0 ml-3" value="Delete" />
            </div>
            <table class="table">
            <thead>
                <tr>
                <th scope="col" style={{width:"5%"}}></th>
                <th scope="col" style={{width:"25%"}}>Name</th>   
                <th scope="col" style={{width:"15%"}}>Gender</th> 
                <th scope="col" style={{width:"20%"}}>Moblie Phone</th>
                <th scope="col" style={{width:"15%"}}>Nationality</th>
                <th scope="col" style={{width:"20%"}}>Action</th>
                </tr>
            </thead>
            <tbody>  
                {user !== null 
                ? user.map((users) => (  
                    <tr key={users.id}> 
                    <th  scope="row"><input class="" type="checkbox" value="" id="defaultCheck1" /></th>
                    <td>{users.user_title+". "+users.user_firstname+" "+users.user_lastname}</td> 
                    <td>{users.user_gender}</td> 
                    <td>{users.user_phoneType+users.user_mobliephone}</td>  
                    <td>{users.user_nationality}</td>
                    <td>  
                        <a href="#" onClick={() => handleUpdate(users.id)}> Edit</a> /
                        <a href="#" onClick={() => handleDelete(users.id)}> Delete </a>
                        {((users.editing) == false) ? "False" : "True"}
                    </td> 
                    </tr>
                ))  
                : <tr> 
                    <td>Empty</td>
                </tr> 
                } 
                {/* <tr> 
                <th scope="row"><input class="" type="checkbox" value="" id="defaultCheck1" /></th>
                <td>{console.log(users.user_firstname)}</td>  
                <td>Otto</td> 
                <td>@mdo</td> 
                <td>Otto</td>
                <td>
                    <a href="">Edit </a>/
                    <a href=""> Delete</a>
                </td>
                </tr> */}
              
            </tbody>
            </table>
        </div>
    );
}

// const mapStateToProps = (state) =>{
//     return{
//         user: state
//     } 
// } 
 
export default List;
